import React from 'react'

function Display() {
    const [calculator, setCalculator] = React.useState({
        displayValue: 0,
        firstOperand: null,
        waitingForSecondOperand: false,
        operator: null,
    });
function eventHandlerOperators(e){
    e.preventDefault();
    switch (e){
       case '*':
        setCalculator.operator = e;
        break;
       case '/': 
        setCalculator.operator = e;
        break;
       case '+':
        setCalculator.operator = e;
        break;
       case '-':
        setCalculator.operator = e;
        break;  
    }
}
  return (
    <div>
        <input type="text" className='calculator-screen' value="" disabled></input>
        <div className='calculator-keys'>
            {/*operator keys */}
            <button type='button' className='operator' value='*' onClick={eventHandlerOperators(value)} >*</button>
            <button type='button' className='operator' value='/' >/</button>
            <button type='button' className='operator' value='+' >+</button>
            <button type='button' className='operator' value='-' >-</button>
            {/** 7, 8, and 9 keys */}
            <button type='button' value='7'>7</button>
            <button type='button' value='8'>8</button>
            <button type='button' value='9'>9</button>
            {/** 4, 5, and 6 keys */}
            <button type='button' value='4'>4</button>
            <button type='button' value='5'>5</button>
            <button type='button' value='6'>6</button>
            {/** 1, 2, and 3 keys */}
            <button type='button' value='1'>1</button>
            <button type='button' value='2'>2</button>
            <button type='button' value='3'>3</button>
            {/** 0, ., AC, and = keys */}
            <button type='button' value='0'>0</button>
            <button type='button' className='operator' value='=' >=</button>
            <button type='button' className='all-clear' value='all-clear'>AC</button>
            <button type='button' className='decimal' value='.'>.</button>
        </div>
    </div>
  )
}

export default Display
