import React from 'react'

function Display() {

    let [calculator, setCalculator] = React.useState({
        displayValue: 0,
        firstOperand: null,
        waitingForSecondOperand: false,
        operator: null,
    });
function eventHandlerOperators(e){
    const {value} = e.target
    console.log(value);
    switch (value){
       case '*':
        setCalculator((prevState) => ({
            ...prevState,
            waitingForSecondOperand: true,
            operator: value,
        }));
        console.log('times');
        break;
       case '/': 
        setCalculator((prevState) => ({
            ...prevState,
            waitingForSecondOperand: true,
            operator: value,
        }));
        console.log('div');
        break;
       case '+':
        setCalculator((prevState) => ({
            ...prevState,
            waitingForSecondOperand: true,
            operator: value,
        }));
        console.log('plus');
        break;
       case '-':
        setCalculator((prevState) => ({
            ...prevState,
            waitingForSecondOperand: true,
            operator: value,
        }));
        console.log('minus');
        break;  
       default:
        console.log('default');
        break; 
    }

}
function log(){    
    console.log(calculator.operator);
}
  return (
    <div>
        <input type="text" className='calculator-screen' value="" disabled></input>
        <div className='calculator-keys'>
            {/*operator keys */}
            <button type='button' className='operator' value='*' onClick={eventHandlerOperators} >*</button>
            <button type='button' className='operator' value='/' onClick={eventHandlerOperators}>/</button>
            <button type='button' className='operator' value='+' onClick={eventHandlerOperators}>+</button>
            <button type='button' className='operator' value='-' onClick={eventHandlerOperators}>-</button>
            {/** 7, 8, and 9 keys */}
            <button type='button' value='7' onClick={log}>7</button>
            <button type='button' value='8'>8</button>
            <button type='button' value='9'>9</button>
            {/** 4, 5, and 6 keys */}
            <button type='button' value='4'>4</button>
            <button type='button' value='5'>5</button>
            <button type='button' value='6'>6</button>
            {/** 1, 2, and 3 keys */}
            <button type='button' value='1'>1</button>
            <button type='button' value='2'>2</button>
            <button type='button' value='3'>3</button>
            {/** 0, ., AC, and = keys */}
            <button type='button' value='0'>0</button>
            <button type='button' className='operator' value='=' >=</button>
            <button type='button' className='all-clear' value='all-clear'>AC</button>
            <button type='button' className='decimal' value='.'>.</button>
        </div>
    </div>
  )
}

export default Display;
